def toBase(b,x):    #convert a number x to an array representing x in base b
    out=[]
    while x!=0:
        out+=[x%b]
        x=x//b      #integer division
    return out[::-1]

def arrToNum(b,a):  #get a number x from an array representing x in base b
    out=0
    for i in a:
        out*=b
        out+=i
    return out

def strToNum(s,base=256):    #convert a number into a string
    return arrToNum(base,[ord(i) for i in s])

def numToStr(n,base=256):    #convert a string into a number
    return "".join([chr(i) for i in toBase(base,n)])


